import "./style.css";
import React, { useState } from "react";
import { BrowserRouter as Router, Switch, Link, Route } from "react-router-dom";
import Home from "./component/Home";
import Navbar from "./component/Navbar";
import About from "./component/About";
import Projects from "./component/Projects";
import Socials from "./component/Socials";

function App() {
  const [displayPage, setPage] = useState("home");

  return (
    <Router>
      <nav className="navbar">
        <div className="navbarLeft">
          <Link to="/" className="btn btnHome">
            Home
          </Link>
          <Link className="btn btnAbout" to="/about">
            About
          </Link>
        </div>
        <div className="navbarMid">
          <h2 id="navbarMidName">Mark Johnston</h2>
        </div>

        <div className="navbarRight">
          <Link className="btn btnProjects" to="/projects">
            Projects
          </Link>
          <Link className="btn btnSocials" to="/socials">
            Socials
          </Link>
        </div>
      </nav>
      <Switch>
        <Route path="/">
          <Home />
        </Route>
        {displayPage === "home" && <Home showAbout={() => setPage("about")} />}
        {displayPage === "about" && (
          <About showProjects={() => setPage("projects")} />
        )}
        {displayPage === "projects" && <Projects />}
        {displayPage === "socials" && <Socials />}
      </Switch>
    </Router>
  );
}

export default App;
