import React, { forwardRef } from "react";
import Typewriter from "typewriter-effect";
import comet from "../images/myColor1.png";
import { motion } from "framer-motion";
import "animate.css";

const Home = (props, ref) => {
  return (
    <div className="homeBodyContainer">
      <motion.img src={comet} alt="Comet" className="comet comet1"></motion.img>
      <motion.img src={comet} alt="Comet" className="comet comet2"></motion.img>
      <motion.img
        src={comet}
        alt="Comet"
        className="comet comet3"
        animate={{ opacity: 100 }}
        initial={{ rotate: 145 }}
      ></motion.img>
      <p className="homeBodyContainer_p1">Hi, my name is</p>
      <div className="matrixText">
        <Typewriter
          onInit={(typewriter) => {
            typewriter
              .changeDeleteSpeed(30)
              .changeDelay(100)
              .pauseFor(200)
              .typeString("Mark Johnston")
              .start();
          }}
        />
      </div>
      <p className="homeBodyContainer_p2">
        I'm a recent grad of <span>Software Developer</span> specializing in web
        development. Creating unique experiences through the web.
      </p>
      <div className="btnOneContainer">
        <a href="#" className="homeBtnOne">
          About Me
        </a>
      </div>
      <div className="btnTwoContainer">
        <a href="#" className="homeBtnTwo">
          My Projects
        </a>
      </div>
      <div className="btnThreeContainer">
        <a href="#" className="homeBtnThree">
          Contact Me
        </a>
      </div>
    </div>
  );
};

export default forwardRef(Home);
