import React from "react";
import earth from "../images/earth.png";
import moon from "../images/moon.png";
import { useInView } from "react-intersection-observer";
import { motion, useScroll, useTransform } from "framer-motion";

export default function ParallaxEffect() {
  const { ref: myRef, inView } = useInView();
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001,
  });
  return (
    <div className="parallaxContainer">
      <motion.div src={earth} alt="Earth" className="foreground" />
      <img src={moon} alt="Moon" className="background"></img>
      <h1 className="parallaxTitle">
        Together we can create and explore the Universe!
      </h1>
      <div className="parallaxRefDiv" ref={myRef}></div>
    </div>
  );
}
