import React, { useRef, useState } from "react";
import Home from "./Home";
import NavbarMobile from "./NavbarMobile.js";
import About from "./About";
import Projects from "./Projects";
import Contact from "./Contact";
import { createRoutesFromElements } from "react-router-dom";

export default function MobileDisp(props) {
  const homeRef = useRef();
  const aboutRef = useRef();
  const projectsRef = useRef();
  const contactRef = useRef();

  function navigateClick(currentRef) {
    currentRef.current.scrollIntoView({ behavior: "smooth" });
  }

  return (
    <main onClick={props.closeNav} className="mobileDisplayMain">
      {props.navToggle === true && (
        <ul className="navbarMobile">
          <li className="btnMobile homeMobile" onClick={navigateClick}>
            Home
          </li>
          <li
            className="btnMobile aboutMobile"
            onClick={navigateClick(aboutRef)}
          >
            About
          </li>
          <li
            className="btnMobile projectsMobile"
            onClick={navigateClick(projectsRef)}
          >
            Projects
          </li>
          <li
            className="btnMobile contactMobile"
            onClick={navigateClick(contactRef)}
          >
            Contact
          </li>
        </ul>
      )}
      <Home ref={homeRef} />
      <About ref={aboutRef} />
      <Projects ref={projectsRef} />
      <Contact ref={contactRef} />
    </main>
  );
}
