import React from "react";
import { useInView } from "react-intersection-observer";
import pawPrint from "../images/pawprint.png";

export default function SkillsPage() {
  const { ref: myRef, inView } = useInView();
  /*
  <div className="snowPageBlizzardEffect" ref={myRef}>
  <Snowfall
    snowflakeCount={600}
    speed={[0.5, 10]}
    wind={[-0.5, 10]}
    radius={[0.5, 4.5]}
    style={{
      position: "absolute",
      top: "110vh",
      width: "100vw",
      height: "110vh",
    }}
  />
</div>
*/
  return (
    <>
      <img src={pawPrint} class="pawPrint print1" alt="Paw Print"></img>
      <img src={pawPrint} class="pawPrint print2" alt="Paw Print"></img>
      <img src={pawPrint} class="pawPrint print3" alt="Paw Print"></img>
      <img src={pawPrint} class="pawPrint print4" alt="Paw Print"></img>
      <div
        className={
          inView
            ? "skillsPageContainer animate__animated animate_fadeIn"
            : "skillsPageContainer animate___animated animate__fadeOut"
        }
        ref={myRef}
      >
        <div className="skillsContainer1">
          <h2>Hi, I'm</h2>
          <h1 id="myName">Mark Johnston</h1>
          <h1 id="myTitle">Software Developer</h1>
          <h3>
            I have the skills that you need, to create unique responsive
            experiences. Let's create and explore new worlds through the web.
          </h3>
          <div className="skillsContainer1_row2">
            <div
              className={
                inView
                  ? "skillCircle circleHTML animate__animated animate__slideInRight animate__delay-1s"
                  : "skillCircle circleHTML"
              }
            ></div>
            <div
              className={
                inView
                  ? "skillCircle circleCSS animate__animated animate__slideInRight animate__delay-2s"
                  : "skillCircle circleCSS"
              }
            ></div>
            <div
              className={
                inView
                  ? "skillCircle circleJS animate__animated animate__slideInRight"
                  : "skillCircle circleJS"
              }
            ></div>
            <div
              className={
                inView
                  ? "skillCircle circleExpress animate__animated animate__slideInRight"
                  : "skillCircle circleExpress"
              }
            ></div>
            <div
              className={
                inView
                  ? "skillCircle circleNode animate__animated animate__slideInRight"
                  : "skillCircle circleNode"
              }
            ></div>
          </div>
        </div>
        <div className="skillsContainer2">
          <div className="skillsContainer2_row1">
            <h3>HTML</h3>
            <h3>CSS</h3>
            <h3>Javascript</h3>
            <h3>Express</h3>
            <h3>NodeJS</h3>
          </div>
        </div>
      </div>
    </>
  );
}
