import React from "react";
import Snowfall from "react-snowfall";
import { InView } from "react-intersection-observer";

export default function SnowWorld() {
  const { ref: myRef, inView } = useInView();
  return (
    <div className="snowPageContainer">
      <div
        className={
          inView ? "snowPageBlizzardEffect" : "snowpageBlizzardEffect invisible"
        }
        ref={myRef}
      >
        <Snowfall
          snowflakeCount={500}
          style={{
            position: "fixed",
            width: "100vw",
            height: "100vh",
          }}
        />
      </div>
    </div>
  );
}
