import "./style.css";
import React, { useState, useRef } from "react";
import useWindowSize from "use-window-size-v2";
import Home from "./component/Home";
import Navbar from "./component/Navbar";
import About from "./component/About";
import Projects from "./component/Projects";
import Contact from "./component/Contact";
import MobileDisp from "./component/MobileDisp";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function App() {
  const { width } = useWindowSize();
  const [navToggle, setNavToggle] = useState(false);
  const homeRef = useRef();
  const aboutRef = useRef();
  const projectsRef = useRef();
  const contactRef = useRef();

  function scrollToDiv(currentRef) {
    currentRef.current.scrollIntoView({ behavior: "smooth" });
  }

  const triggerNavToggle = () => {
    setNavToggle(!navToggle);
  };
  const turnOffNav = () => {
    setNavToggle(false);
  };
  // {navToggle === true && width < 820 && <NavbarMobile />}
  return (
    <>
      {width >= 768 && <Navbar navOff={turnOffNav} scrollTo={scrollToDiv} />}
      {width >= 768 && (
        <>
          <nav className="navbar">
            <div className="navbarLeft">
              <Button
                className="btn btnHome"
                onClick={() => scrollToDiv(homeRef)}
                Home
              ></Button>
              <Link className="btn btnAbout" to="/about">
                About
              </Link>
            </div>

            <div className="navbarMid">
              <h2 id="navbarMidName">Mark Johnston</h2>
            </div>

            <div className="navbarRight">
              <Link className="btn btnProjects" to="/projects">
                Projects
              </Link>
              <Link className="btn btnContact" to="/contact">
                Contact
              </Link>
            </div>
          </nav>
          <Home ref={homeRef} />
          <About ref={aboutRef} />
          <Projects ref={projectsRef} />
          <Contact ref={contactRef} />
        </>
      )}
      {width < 768 && (
        <FontAwesomeIcon onClick={triggerNavToggle} icon="fa-solid fa-bars" />
      )}
      {width < 768 && (
        <main onClick={turnOffNav} className="mobileDisplayMain">
          <ul className="navbarMobile">
            <button
              className="btnMobile homeMobile"
              onClick={() => scrollToDiv(homeRef)}
            >
              Home
            </button>
            <button
              className="btnMobile aboutMobile"
              onClick={() => scrollToDiv(aboutRef)}
            >
              About
            </button>
            <button
              className="btnMobile projectsMobile"
              onClick={() => scrollToDiv(projectsRef)}
            >
              Projects
            </button>
            <button
              className="btnMobile contactMobile"
              onClick={() => scrollToDiv(contactRef)}
            >
              Contact
            </button>
          </ul>

          <Home ref={homeRef} />
          <About ref={aboutRef} />
          <Projects ref={projectsRef} />
          <Contact ref={contactRef} />
        </main>
      )}
    </>
  );
}

export default App;
