import "./style.css";
import React, { useState, useRef } from "react";
import useWindowSize from "use-window-size-v2";
import Home from "./component/Home";
import Navbar from "./component/Navbar";
import About from "./component/About";
import Projects from "./component/Projects";
import Contact from "./component/Contact";
import MobileDisp from "./component/MobileDisp";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function App() {
  const desktopHomeRef = useRef();
  const { width } = useWindowSize();
  const [navToggle, setNavToggle] = useState(false);

  const triggerNavToggle = () => {
    setNavToggle(!navToggle);
  };
  const turnOffNav = () => {
    setNavToggle(false);
  };
  // {navToggle === true && width < 820 && <NavbarMobile />}
  return (
    <>
      {width >= 768 && <Navbar navOff={turnOffNav} />}
      {width >= 768 && (
        <>
          <Home ref={desktopHomeRef} />
          <About />
          <Projects />
          <Contact />
        </>
      )}
      {width < 768 && (
        <FontAwesomeIcon onClick={triggerNavToggle} icon="fa-solid fa-bars" />
      )}
      {width < 768 && (
        <>
          <MobileDisp closeNav={turnOffNav} navToggle={navToggle} />
        </>
      )}
    </>
  );
}

export default App;
