import React from "react";
import space from "../images/space.jpg";
import mountainRange from "../images/mountainForeground.png";
import { useInView } from "react-intersection-observer";

export default function ParallaxEffect() {
  const { ref: myRef, inView } = useInView();
  return (
    <div
      className={
        inView
          ? "parallaxContainer animate__animated animate__fadeIn"
          : "parallaxContainer animate__animated animate__fadeOut"
      }
      ref={myRef}
    >
      <img src={space} alt="Space" className="background"></img>
      <img
        src={mountainRange}
        alt="Mountain Range"
        className="foreground"
      ></img>
      <h1 className="parallaxTitle">
        Together we can create and explore the Universe!
      </h1>
    </div>
  );
}
